import React, { useState } from 'react'
import { Card } from 'primereact/card';
import { Button } from 'primereact/button';
import { InputText } from 'primereact/inputtext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const NEStudent = () => {
    const endpoint = 'http://127.0.0.1:8000/api';

    let navigate = useNavigate();

    function navigateTo(string){
    navigate(string);
    }

    const footer = (
        <span>
            <Button label="Save" onClick=''icon="pi pi-check" />
            <Button label="Cancel" icon="pi pi-times" className="p-button-secondary ml-2" />
        </span>
    );
    
    const [student, setStudent] = useState(
        {
            id: 0,
            nombre: '',
            matricula: '',
            edad: '',
            sexo: '',
        }
    );

    const guardarDatos = async (e) => {
        e.preventDefault();
        await axios.post(`${endpoint}/alumno`, student)
        .then((response) => {
            console.log("Guardando...")
            console.log(response.data)
        }).catch((error) => {

        })
        navigateTo('/')
    }

    const inputChange = (event) =>{
        console.log("handleInputChange")
        console.log(event.target.name)
        console.log(event.target.value)
        
        setStudent ({
            ...student,
            [event.target.name] : event.target.value
        })
    };

  return (
    <div>
        <Card title="Advanced Card" subTitle="Subtitle" style={{ width: '25em' }} footer={footer}>
            
        <span className="p-float-label">
            <InputText id='nombre' value={student.nombre} onChange={inputChange} name='nombre'/>
            <label htmlFor="nombre">Nombre</label>
        </span>
        <br></br>
        <span className="p-float-label">
            <InputText id='matricula' value={student.matricula} onChange={inputChange} name='matricula'/>
            <label htmlFor="matricula">Matricula</label>
        </span>
        <br></br>
        <span className="p-float-label">
            <InputText id='sexo' value={student.sexo} onChange={inputChange} name='sexo'/>
            <label htmlFor="sexo">Sexo</label>
        </span>
        <br></br>
        <span className="p-float-label">
            <InputText id='edad' value={student.edad} onChange={inputChange} name='edad'/>
            <label htmlFor="edad">Edad</label>
        </span>
        <br></br>
        </Card>
    </div>
  )
}

export default NEStudent
